import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Conversation from "./components/Conversation";
import convServices from "./services/conversationServices";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      conversations: [],
    }
  }

  conversationMapper(convos) {
    return convos.map(convo => <Conversation data/>)
  }

  componentDidMount() {
    convServices.selectAll().then(convos => {
      this.setState(prevState => ({ conversations: this.conversationMapper(convos) }));
    })
  }

  render() {
    return (
      <div className="wrapper">
        <div className="container">
          {JSON.stringify(this.state.resp)}
        </div>
      </div>
    );
  }
}

export default App;
