import React from "react";
import Message from "./Message";
import messageServices from "../services/messageServices";

class Conversation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
            input: "",
            messages: [],
        }
    }

    createMessage = () => {
        const data = {
            title: this.state.input
        }
        messageServices.create(data).then(() => {
            const newMessage = this.messageMapper([data]);
            this.setState(prevState => ({
                messages: prevState.messages.concat(newMessage),
                input: "",
            }))
        }).catch(err => console.error(err))
    }

    handleInputChange = (event) => {
        this.setState(prevState => ({
            input: event.target.value
        }))
    }

    toggleOpen = () => {
        this.setState(prevState => ({
            open: !prevState.open
        }))
    }

    messageMapper(convos) {
        return convos.map(convo => <Message key={convo.Id} data={convo} />)
    }

    componentDidMount() {
        messageServices.selectByConversationId(this.props.data.Id).then(messages => {
            this.setState(prevState => ({ messages: this.messageMapper(messages) }));
        })
    }

    render() {
        const { title, dateCreated, dateModified } = this.props.data;
        return (
            <div className="conversation-wrapper">
                <div className="conversation" onClick={this.toggleOpen}>
                    <div className="title">{title}</div>
                    <div className="info">
                        <div className="created">{dateCreated}</div>
                        <div className="modified">{dateModified}</div>
                    </div>
                    {this.state.open &&
                        <div className="drawer">
                                {this.state.messages || null}
                            <div className="button-container">
                                <input className="new-text" value={this.state.input} onChange={this.handleInputChange} onClick={event} />
                                <div className="create" onClick={this.createMessage}>Create</div>
                            </div></div>
                    }
                </div>
            </div>
        )
    }
}

export default Conversation;
