import React from "react";
import Message from "./Message";
import messageServices from "../services/messageServices";

class Conversation extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false,
            messages: [],
        }
    }

    toggleOpen = () => {
        this.setState(prevState => ({
            open: !prevState.open
        }))
    }

    messageMapper(convos) {
        return convos.map(convo => <Message key={convo.Id} data={convo} />)
    }

    componentDidMount() {
        messageServices.selectByConversationId(this.props.data.Id).then(messages => {
            this.setState(prevState => ({ messages: this.messageMapper(messages) }));
        })
    }

    render() {
        const { Title, DateCreated, DateModified } = this.props.data;
        return (
            <div className="conversation-wrapper">
                <div className="conversation" onClick={this.toggleOpen}>
                    <div className="title">{Title}</div>
                    <div className="info">
                        <div className="created">{new Date(DateCreated).toLocaleString()}</div>
                        <div className="modified">{DateModified}</div>
                    </div>
                    {this.state.open &&
                        <div className="drawer">{this.state.messages || null}</div>
                    }
                </div>
            </div>
        )
    }
}

export default Conversation;
