import axios from "axios";

const cAxios = axios.create({
  baseURL: `http://localhost:8080/conversations/`,
});

console.log(process.env);

const globalHandler = (config) => {
  return cAxios(config).then(handleSuccess).catch(handleError);
};

const handleSuccess = (res) => {
    res.data.
    console.log("Data: ", res.data)
    if (res.data["DateCreated"]) res.data["DateCreated"] = new Date(res.data["DateCreated"]).toLocaleString();
    if (res.data["DateModified"]) res.data["DateModified"] = new Date(res.data["DateModified"]).toLocaleString();
  return res.data;
};

const handleError = (err) => {
  return Promise.reject(err);
};

const create = (data) => {
  const config = {
    url: "create",
    method: "POST",
    headers: { "Content-Type": "application/json" },
    data,
  };

  return globalHandler(config);
};

const selectAll = () => {
  const config = {
    url: "",
    method: "GET",
    headers: { "Content-Type": "application/json" },
  };

  return globalHandler(config);
};

const selectById = (id) => {
  const config = {
    url: `${id}`,
    method: "GET",
    headers: { "Content-Type": "application/json" },
  };

  return globalHandler(config);
};

const update = (id, data) => {
  const config = {
    url: `${id}/update`,
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    data,
  };

  return globalHandler(config);
};

const remove = (id) => {
  const config = {
    url: `${id}/delete`,
    method: "DELETE",
    headers: { "Content-Type": "application/json" },
  };

  return globalHandler(config);
};
const services = { create, selectAll, selectById, update, remove };
export default services;
