import React from "react";

const Thought = (props) => {
    const { text, dateCreated, DateModified } = this.props.data;
    return (
        <div className="thought-wrapper">
            <div className="thought">
                <div className="text">{text}</div>
                <div className="info">
                    <div className="created">{dateCreated}</div>
                    <div className="modified">{DateModified}</div>
                </div>
                {this.state.open &&
                    <div className="drawer">{this.state.thoughts || null}</div>
                }
            </div>
        </div>
    )
}

export default Thought;
